import React, { useState } from 'react';
import Table from './Table';
import { useOutletContext } from 'react-router-dom'
import './style.css'

const DisplayBlogs = () => {

    const [blogs] = useOutletContext()
    const [dynamicValues, setDynamicValues] = useState(blogs)
    const [StartDate, setStartDate] = useState();
    const [EndDate, setEndDate] = useState();
    const header = ['Subject', 'Discription', 'CreatedDate']


    const handelchangemin = (e) => {
        e.preventDefault()
        setStartDate(e.target.value)
        setDynamicValues(blogs.filter((obj) => obj.date >= StartDate))
    }
    const handelchangemax = (e) => {
        e.preventDefault()
        setEndDate(e.target.value)
        setDynamicValues(blogs.filter((obj) => obj.date <= EndDate))
    }


    const handelsubit = (e) => {
        e.preventDefault()
        if (StartDate || EndDate) {
            if (StartDate) {
                setDynamicValues(blogs.filter((obj) => obj.date >= StartDate))
            }
            if (EndDate) {
                setDynamicValues(dynamicValues.filter((obj) => obj.date <= EndDate))
            }
            console.log(dynamicValues);
        }
    }
    const handelclear = (e) => {
        e.preventDefault()
        setStartDate('')
        setEndDate('')
        setDynamicValues(blogs)
    }

    return (
        <>
            <div>
                <form onSubmit={handelsubit} className='formSerch'>
                    <label>Min Date</label>
                    <div className='serchInput'>
                        <input type="date" name='startDate' value={StartDate} onChange={handelchangemin} />
                    </div>
                    <br />
                    <br />
                    <label>Max Date</label>
                    <div className='serchInput' >
                        <input type="date" name='EndDate' value={EndDate} onChange={(e) => setEndDate(e.target.value)} />
                    </div>
                    <br />
                    <button className='btn'>Serch</button>
                    <button className='btn' type='button' onClick={handelclear}>Clear</button>
                </form>
                <br />
                <br />
                <br />
            </div >
            <dir>

                <Table data={dynamicValues} header={header} />
            </dir>
        </>
    );
}

export default DisplayBlogs;
