import React, { useState } from 'react';
import './post.css'

const Table = ({ data, header, setBlogs }) => {

    const [comment, setcomment] = useState('');

    const handelReplay = (e, index) => {
        setBlogs((prev) => ([...prev, data[index].comments.push(comment)]))
        document.getElementsByName('comment')[index].value = ''
    }
    const handelLikes = (e, index) => {
        setBlogs((prev) => ([...prev, data[index].likes]))
    }



    return (
        <div>
            {data.map((item, index) =>
                <>
                    <div key={index} className='post'>
                        <div className='title'>
                            <h2>{item.subject}</h2>
                            <h5>{item.date}</h5>
                        </div>
                        <p>{item.discription}</p>
                        <div>
                            <button className='btn' onClick={(e) => handelLikes(e, index)}>Like</button>
                            <p>{item.likes}</p>
                        </div>
                        <br />
                        <div>
                            <input type="text" name='comment' onChange={(e) => setcomment(e.target.value)} />
                            <button className='btn' onClick={e => handelReplay(e, index)}>replay</button>
                        </div>

                    </div>
                    {
                        item.comments.map((m, i) => (
                            <p key={i}>{m}</p>
                        ))
                    }
                </>
            )}

        </div>
    );
}

export default Table;




















