import React, { useState } from 'react';
import { Link, NavLink, Outlet } from 'react-router-dom';
import Banner from '../Components/Banner/Banner';

const Blogs = () => {

    let activeStyle = {
        backgroundColor: 'rgb(0, 0, 0)',
        color: 'white'
    };



    const [blogs, setBlogs] = useState([
        {
            subject: 'WorldCub',
            discription: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Praesentium dicta minima et ipsum sapiente corporis aliquid aspernatur dolorum quaerat, perspiciatis consequatur velit, rem quisquam magni alias facere deserunt possimus repellendus',
            date: '2021-01-01',
            likes: 5,
            comments: ['Great', 'Awsome']
        },
        {
            subject: 'FootBall',
            discription: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Praesentium dicta minima et ipsum sapiente corporis ',
            date: '2021-06-05',
            likes: 7,
            comments: []
        },
        {
            subject: 'FootBall',
            discription: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Praesentium dicta minima et ipsum sapiente corporis ',
            date: '2021-09-05',
            likes: 9,
            comments: []
        },
        {
            subject: 'FootBall',
            discription: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Praesentium dicta minima et ipsum sapiente corporis ',
            date: '2022-02-05',
            likes: 4,
            comments: []
        },
    ]);
    return (
        <div>

            <Banner
                pageTitle={'Blog.'}
                subtitle={'Here is Some of my Posts that i have made '}
            />
            <div className='navigationWrapper'>
                <NavLink
                    className='navlink'
                    to="displayBlogs"
                    style={({ isActive }) =>
                        isActive ? activeStyle : undefined
                    }
                >
                    List All Posts

                </NavLink>
                <NavLink
                    className='navlink'
                    to="createBlogs"
                    style={({ isActive }) =>
                        isActive ? activeStyle : undefined
                    }
                >
                    Create Post
                </NavLink>
            </div>

            <div className='outile'>
                <Outlet context={[blogs, setBlogs]} />
            </div>
        </div>
    );
}

export default Blogs;
