import React, { useState } from 'react';
import { useOutletContext } from 'react-router-dom';
import Table from './Table';

const DisplayContacts = () => {
    const [contacts] = useOutletContext()
    const [typeserch, setTypeserch] = useState(0);
    const [serch, setSetserch] = useState('');
    const header = ['Name', 'Phone', 'Email']

    const handelsubmit = (e) => {
        e.preventDefault()
        const aa = contacts.filter(obj => obj.name = typeserch)
        console.log(aa);
    }





    return (
        <div>
            <form onSubmit={handelsubmit}>
                <div>
                    <label>Search</label>
                    <div>
                        <select name="pets" id="pet-select" onChange={(e) => setTypeserch(e.target.value)}>
                            <option value={0}>Name</option>
                            <option value={1}>phone</option>
                            <option value={2}>Email</option>
                        </select>
                        <input type="text" onChange={(e) => setSetserch(e.target.value)} />
                    </div>
                </div>
                <button className='btn'>Serch</button>
            </form>
            DisplayContacts
            <Table header={header} data={contacts} />
        </div>
    );
}

export default DisplayContacts;
